import React, { useState } from 'react';
import './SubSearchBar.css';

function SubSeachBar({ onSearch }) {
    const [searchTerm, setSearchTerm] = useState('');

    const handleSubmit = (event) => {
      event.preventDefault();
      // here goes the logic for searching 
      console.log(searchTerm);
    };

  return (
    <form onSubmit={handleSubmit} className="sub-search-bar">
      <input
        type="text"
        className="sub-search-input"
        placeholder="Search Here" // this is the text that appears on the search bar
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
      />
      <button type="submit" className="sub-search-button">
        Enter
      </button>
    </form>
  );
}   

export default SubSeachBar;
